

var fs = require('fs');

// iBuildText.build_text('c:\\data_sec.txt', JSON.stringify(res))

function build_text(filename,data)
{
  //  fs.appendFile(filename, data, function (err) //{
    fs.appendFile(filename, data, encoding = 'utf8', function (err)
    {
        if (err) {
            console.log(err.message);
        } else {
            console.log("text saved to " + filename);
        }
    });
}

exports.build_text = build_text;
