var url = "http://61.91.14.253:9003/api/"; //"http://127.0.0.1:9003/api/"; // //config.camera.server;// 
 
function send_data(api_name, json_data, callback) {
    var master_url = url + api_name;

  //  console.log(master_url);

    request({
        url: master_url,
        method: "POST",
        json: true,   // <--Very important!!!
        body: json_data
    }, function (error, response, body) {
        debugger;
        if (error) {
            console.log(error);
            callback(error);
            return;
        }
        //console.log(body);
        callback(body);
        return;
       
    });
}

exports.send_data = send_data;